mapper.select:
SELECT COUNT(*)
FROM APPROVAL A
JOIN EMP E ON (A.APP_WRITE_NO = E.EMPNO)


		AND APP_CHECK_PROGRESS = #{approval_status}


		AND APP_WRITE_NO = #{userNo}

SELECT * FROM ( select ROWNUM AS RNUM, A.* from ( SELECT A.APP_SEQ, A.APP_WRITE_NO, A.APP_WRITE_SYS, A.FIRST_TIME_APPROVER, A.INTERIM_APPROVER, A.FINAL_APPROVER, A.APP_CHECK_SYSDATE, A.APP_CHECK_PROGRESS, A.APP_ORIFILENAME, A.APP_REFILENAME, E.NAME, E.POSITION, A.APPROVAL_KINDS, E.DEPTNO, L.LOA_TITLE, LEA.LEAVE_CLASSIFY, ER.ER_TITLE FROM APPROVAL A JOIN EMP E ON (A.APP_WRITE_NO = E.EMPNO) LEFT JOIN APP_LOA L ON (A.APP_SEQ = L.LOA_APP_SEQ) LEFT JOIN APP_LEAVE LEA ON (A.APP_SEQ = LEA.LEAVE_APP_SEQ) LEFT JOIN APP_RECEIVE_REF arr ON (A.APP_SEQ = arr.RECEIVE_REF_APP_SEQ) LEFT JOIN APP_ER ER ON (A.APP_SEQ = ER.ER_APP_SEQ)
AND APP_CHECK_PROGRESS = #{approval_status}
AND APP_WRITE_NO = #{userNo}
ORDER BY APP_SEQ DESC ) A
WHERE ROWNUM <= #{endNo} ) WHERE #{startNo} <= RNUM

SELECT COUNT(*) FROM approval




				APPROVAL_KINDS = #{searchobj}


				USER_NAME = #{searchobj}


				APP_CHECK_PROGRESS = #{searchobj}

select a.*, b.loa_title, c.leave_classify, d.er_title, e.deptno from (
	select rownum rnum, TT.* from (
		select * from approval




						APPROVAL_KINDS = #{searchobj}


						USER_NAME = #{searchobj}


						APP_CHECK_PROGRESS = #{searchobj}





	) TT where rownum <= #{endNo}
) a
	left outer join APP_LOA b on a.APP_SEQ = b.LOA_APP_SEQ
	left outer join APP_LEAVE c on a.APP_SEQ = c.LEAVE_APP_SEQ
	left outer join APP_ER d on a.APP_SEQ = d.ER_APP_SEQ
	join EMP e on e.EMPNO = a.APP_WRITE_NO

	where a.rnum >= #{startNo}
	order by APP_SEQ desc

mapper.update:
update approval set
    APP_CHECK_PROGRESS = '반려',
    APP_REASON = #{reason}
where
	APP_SEQ = #{seq}
