mapper.select:


select * from (   
	select rownum rnum, TT.* from (
		select a.empno, b.deptno, b.name, a.attdate, a.intime, a.outtime, a.worktime, a.etc, a.deducedate
	    	from attend a inner join emp b
	   		on a.empno = b.empno
	   		order by attdate desc, empno asc
	) TT where rownum <= #{endNo}
) where rnum >= #{startNo}



select * from (   
	select rownum rnum, TT.* from (
		select a.empno, b.deptno, b.name, a.attdate, a.intime, a.outtime, a.worktime, a.etc, a.deducedate
	    	from attend a inner join emp b
	   		on a.empno = b.empno
	   		where deptno=#{deptno}
	   		order by attdate desc, empno asc
	) TT where rownum <= #{endNo}
) where rnum >= #{startNo}



select * from (   
	select rownum rnum, TT.* from (
		select a.empno, b.deptno, b.name, a.attdate, a.intime, a.outtime, a.worktime, a.etc, a.deducedate
	    	from attend a inner join emp b
	   		on a.empno = b.empno
	   		where TO_CHAR(attdate, 'YYYY-MM-DD') = #{searchdate}
	   		order by attdate desc, empno asc
	) TT where rownum <= #{endNo}
) where rnum >= #{startNo}



select * from (   
	select rownum rnum, TT.* from (
		select a.empno, b.deptno, b.name, a.attdate, a.intime, a.outtime, a.worktime, a.etc, a.deducedate
	    	from attend a inner join emp b
	   		on a.empno = b.empno
	   		where name like '%'||#{searchname}||'%'
	   		order by attdate desc, empno asc
	) TT where rownum <= #{endNo}
) where rnum >= #{startNo}



select * from (   
	select rownum rnum, TT.* from (
		select a.empno, b.deptno, b.name, a.attdate, a.intime, a.outtime, a.worktime, a.etc, a.deducedate
	    	from attend a inner join emp b
	   		on a.empno = b.empno
	   		where deptno = #{searchdeptno} and TO_CHAR(attdate, 'YYYY-MM-DD') = #{searchdate}
	   		order by attdate desc, empno asc
	) TT where rownum <= #{endNo}
) where rnum >= #{startNo}



select * from (   
	select rownum rnum, TT.* from (
		select a.empno, b.deptno, b.name, a.attdate, a.intime, a.outtime, a.worktime, a.etc, a.deducedate
	    	from attend a inner join emp b
	   		on a.empno = b.empno
	   		where TO_CHAR(attdate, 'YYYY-MM-DD') = #{searchdate} and name like '%'||#{searchname}||'%'
	   		order by attdate desc, empno asc
	) TT where rownum <= #{endNo}
) where rnum >= #{startNo}


select * from (   
	select rownum rnum, TT.* from (
		select a.empno, b.deptno, b.name, a.attdate, a.intime, a.outtime, a.worktime, a.etc, a.deducedate
	    	from attend a inner join emp b
	   		on a.empno = b.empno
	   		where a.empno = #{empno} and etc is not null
	   		order by attdate desc, empno asc
	) TT where rownum <= #{endNo}
) where rnum >= #{startNo}
